changeSet.createProcedure:
CREATE OR REPLACE FUNCTION public.get_allusers( f_login character varying, f_tenantid bigint) RETURNS setof trakeye_user LANGUAGE 'sql' COST 100.0 VOLATILE NOT LEAKPROOF AS $function$
WITH RECURSIVE usertree AS ( SELECT a.* FROM trakeye_user a WHERE a.login = f_login and a.tenant_id = f_tenantid UNION ALL SELECT c.* FROM trakeye_user c JOIN usertree p ON c.created_by = p.login ) SELECT a.* FROM usertree a ;
$function$;
ALTER FUNCTION public.get_allusers(character varying, bigint) OWNER TO trakeye;
